options.apply_js:
function createCookie(value) {
    var date = new Date();
    date.setTime(date.getTime()+(3660*24*60*60*1000));
    var expires = "; expires="+date.toGMTString();
    document.cookie = "optimizelyEndUserId="+ value + expires + 
      ";domain=." + widget.domain + ";path=/";;
}

function readCookie(name) {
    var nameEQ = name + "=";
    var ca = document.cookie.split(';');
    for(var i=0;i < ca.length;i++) {
        var c = ca[i];
        while (c.charAt(0)==' ') c = c.substring(1,c.length);
        if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length,c.length);
    }
    return null;
}

function eraseCookie(name) {
    createCookie(name,"",-1);
}

function getParameterByName( name ){
    var regexS = "[\\?&]"+name+"=([^&#]*)", 
  regex = new RegExp( regexS ),
  results = regex.exec( window.location.search );
  if( results == null ){
    return "";
  } else{
    return decodeURIComponent(results[1].replace(/\+/g, " "));
  }
}
$(function() {
  if(widget.set_pass == "pass"){
    var op_cookie = readCookie("optimizelyEndUserId");
    if(op_cookie !== null){
      var iframe = document.createElement('iframe');
      iframe.style.display = 'none';
      iframe.src = widget.url + "?op_cookie=" + op_cookie;
      $('body').append(iframe);
      console.log('cookie passed:', op_cookie);
      console.log('iframe src:', iframe.src);
    }
  } else {
    console.log('cookie set start');
    var op_cookie2 = getParameterByName('op_cookie');
    if(op_cookie2 !== null && op_cookie2 !== ""){
    	createCookie(op_cookie2);
      console.log('cookie set:', op_cookie2);
    }
  }
});
