options.apply_js:
$(document).ready(function() {
  if (widget.activation_type == "all") {
    setTimeout(function(){ 
      var op_showOnce = true;
      console.log("Manual Codition Running: All");
      $('.simpleFormSection').bind("DOMSubtreeModified",function(){
        if(op_showOnce && $(widget.selector).length > 0){
          window.optimizely = window.optimizely || [];
    			window.optimizely.push({type: 'activate'});
        }
      });
    }, 5000);
  } else if (widget.activation_type == "page") {
    setTimeout(function(){ 
      var op_showOnce = true;
      console.log("Manual Codition Running: Page");
      $('.simpleFormSection').bind("DOMSubtreeModified",function(){
        if(op_showOnce && $(widget.selector).length > 0){
          window.optimizely = window.optimizely || [];
          window.optimizely.push({
            type: "page",
            pageName: widget.page
          });
        }
      });
    }, 5000);
  }
});
